glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform Properties {
  vec4 colors[6];
  vec4 heights[6];
  vec2 iResolution;
  float skewAngle;
  float waveType;
};
vec2 tranPt(vec2 uv,float angle,vec2 center){
  float c = cos(angle);
  float s = sin(angle);
  float Ox = center.x;
  float Oy = center.y;
  mat3 m = mat3(
        c,0.0-s,(1.0-c)*Ox+s*Oy,
        s,c,(1.0-c)*Oy-s*Ox,
        0.0,0.0,1.0
  );
  vec3 ret = m*vec3(uv.x,uv.y,0.0);
  return ret.xy;
}
float drawWater(vec2 uv,float angle,float _height,int arrSize,int curIdx)
{
  float ratio = iResolution.y/iResolution.x;
  bool toLeft = sin(angle)>=0.0;
  vec2 center = vec2(0.5,1.0-_height);
  float _t = abs(tan(angle));
  if(_height<0.5){
    bool is_bottom = _t/ratio>2.0*_height;
    if(is_bottom){
      center.x = sqrt(2.0*_height/_t*ratio)/2.0;
      center.y = 1.0 - sqrt(2.0*_height*_t/ratio)/2.0;
      bool is_top = _t>(ratio)/(_height*2.0);
      if(is_top){
        center.y = 0.5;
        center.x = _height;
      }
    }
    if(!toLeft){
      center.x = 1.0-center.x;
    }
  }else{
    bool is_top = _t>2.0*ratio*(1.0-_height);
    if(is_top){
      center.x = sqrt(2.0*ratio*(1.0-_height)/_t)/2.0;
      center.y = sqrt(2.0*ratio*(1.0-_height)*_t)/2.0/ratio;
      bool is_bottom = _t>ratio/(2.0*(1.0-_height));
      if(is_bottom){
        center.y = 0.5;
        center.x = 1.0-_height;
      }
    }
    if(toLeft){
      center.x = 1.0-center.x;
    }
  }
  uv.y = uv.y*ratio;
  uv -= vec2(center.x,center.y*ratio);
  vec2 uv1 = tranPt(uv,angle,vec2(0.0));
  float y = 0.0;
  bool hasWave = curIdx==arrSize-1;
  hasWave = hasWave;
  if(hasWave){
    float amplitude = 0.0;
    float angularVelocity = 0.0;
    float frequency = 0.0;
    if(abs(waveType-1.0)<0.01){
      amplitude = 0.06;
      angularVelocity = 10.0;
      frequency = 10.0;
    }else if(abs(waveType-2.0)<0.01){
      amplitude = 0.03;
      angularVelocity = 5.0;
      frequency = 6.0;
    }
    y = amplitude * sin((angularVelocity * uv1.x) + (frequency * cc_time.x)*(toLeft ? 1. : -1.));
  }
  float alpha = (uv1.y > y) ? 1.0 : 0.0;
  return alpha;
}
void main () {
  vec4 ret = vec4(1.0,1.0,1.0,1.0);
  ret *= texture(texture, v_uv0);
  if(ret.a == 0.0){
    discard;
  }
  float angle = mod(skewAngle,360.0) * 3.14159265358979323846 / 180.0;
  vec2 uv = vec2(v_uv0);
  float a = 0.0;
  float _height = 0.0;
  int size = 0;
  for(int i=0;i<6;i++){
    if(heights[i].x<0.001){
      continue;
    }
    size++;
  }
  for(int i=0;i<6;i++){
    if(heights[i].x<0.001){
      continue;
    }
    _height+=heights[i].x;
    a += drawWater(uv,angle,_height,size,i);
    if(a>0.0){
        ret *= a*colors[i];
        break;
    }
  }
  if(a<0.001){
    discard;
  }
  gl_FragColor = ret;
}

